import 'package:flutter/material.dart';import 'package:geolocator/geolocator.dart';class MapAlertDialog extends StatelessWidget {  const MapAlertDialog({    super.key,  });  @override  Widget build(BuildContext context) {    return AlertDialog(      backgroundColor: Theme.of(context).cardColor,      insetPadding: const EdgeInsets.symmetric(horizontal: 16),      title: const Text('Use Permissions for Map'),      content: const Padding(        padding: EdgeInsets.symmetric(horizontal: 25.0, vertical: 8.0),        child: Text('Do you want to give permissions to map'),      ),      actions: <Widget>[        Padding(          padding: const EdgeInsets.symmetric(horizontal: 25.0, vertical: 8.0),          child: Row(            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              ElevatedButton(                  onPressed: () {                    Navigator.of(context).pop();                  },                  child: const Text('Deny')),              ElevatedButton(                  onPressed: () async {                    await Geolocator.openLocationSettings();                  },                  child: const Text('Allow'))            ],          ),        )      ],    );  }}