import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../bloc/charging_cubit.dart';import '../widgets/charging_stations_presenter_widget.dart';class ChargingScreen extends StatelessWidget {  const ChargingScreen({super.key});  @override  Widget build(BuildContext context) {    return BlocProvider<ChargingCubit>(      create: (_) => ChargingCubit(        chargingRepository: RepositoryProvider.of(context),      ),      child: const ChargingStationsPresenterWidget(),    );  }}