import 'package:flutter_bloc/flutter_bloc.dart';import '../../../stations/models/map_marker/map_marker.dart';import 'map_pin_cubit_state.dart';class MapPinCubit extends Cubit<MapPinCubitState> {  MapPinCubit() : super(const MapPinCubitState(mapMarker: null)); // Function to set the map marker in the state  void tapOnMapMarker(CustomMapMarker mapMarker) {    emit(MapPinCubitState(mapMarker: mapMarker));  }}