import 'package:google_maps_flutter/google_maps_flutter.dart';import 'package:hydrated_bloc/hydrated_bloc.dart';class MapCubit extends HydratedCubit<String> {  MapCubit() : super('normal');  void defineMapType(MapType selectedMapType) {    emit(state == selectedMapType.name ? MapType.normal.name : selectedMapType.name);  }  ///todo: rewrite to get whole MapType @override  @override  String fromJson(Map<String, dynamic> json) => json['mapType'] as String;  @override  Map<String, dynamic> toJson(String state) => {'mapType': state};}