import 'package:google_maps_flutter/google_maps_flutter.dart';import 'package:hydrated_bloc/hydrated_bloc.dart';class MapCubit extends HydratedCubit<MapType> {  MapCubit() : super(MapType.normal);  void defineMapType({required MapType selectedMapType}) {    emit(state == selectedMapType ? MapType.normal : selectedMapType);  }  @override  MapType fromJson(Map<String, dynamic> json) {    final mapTypeIndex = json['mapType'] as int?;    return mapTypeIndex != null ? MapType.values[mapTypeIndex] : MapType.normal;  }  @override  Map<String, dynamic> toJson(MapType state) {    return {'mapType': state.index};  }}