import 'package:flutter/material.dart';import '../../theme/theme_info.dart';class MapStatusTextWidget extends StatelessWidget {  const MapStatusTextWidget({    super.key,    required this.statusText,    required this.bgColor,  });  final String? statusText;  final Color bgColor;  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(        color: bgColor,        borderRadius: const BorderRadius.all(Radius.circular(100)),      ),      child: Padding(        padding: const EdgeInsets.symmetric(horizontal: 10, vertical: 5),        child: Text(          statusText ?? '',          style: Theme.of(context).textTheme.labelMedium!.copyWith(color: white),        ),      ),    );  }}