import 'package:extrawest_ui_kit/components/widgets/ew_base_button.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../common/services/logger.dart';import '../auth_bloc/auth_bloc.dart';class ProfileWidget extends StatelessWidget {  const ProfileWidget({super.key, this.profilePhoto, this.email, this.name});  final String? profilePhoto;  final String? email;  final String? name;  @override  Widget build(BuildContext context) {    log.fine('state.user>>>> ${context.watch<AuthBloc>().state.user?.displayName}');    final user = context.watch<AuthBloc>().state.user;    return Expanded(      child: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: [            if (user?.photoURL != null)              CircleAvatar(                radius: 60,                backgroundImage: NetworkImage(                  user?.photoURL ?? '',                ),              ),            const SizedBox(              height: 20,            ),            Column(              children: [                Text(                  user?.displayName ?? '',                  style: Theme.of(context).textTheme.bodyMedium?.copyWith(color: Colors.black),                ),                Text(                  user?.email ?? '',                  style: Theme.of(context).textTheme.bodyMedium?.copyWith(color: Colors.black),                ),                const SizedBox(                  height: 20,                ),                EWBaseButton.filled(                  onPressed: () {                    context.read<AuthBloc>().add(AuthSignOutEvent());                  },                  title: 'Logout',                ),              ],            ),          ],        ),      ),    );  }}