import 'package:flutter_bloc/flutter_bloc.dart';import 'package:google_maps_flutter/google_maps_flutter.dart';import 'package:samoilenko_maps_app/features/stations/bloc/stations_bloc/map_bloc/map_cubit_state.dart';class MapCubit extends Cubit<MapCubitState> {  MapCubit() : super(const MapCubitState.initial());  Future<void> defineMapType(MapType selectedMapType) async {    emit(MapCubitState.changeMapType(mapType: selectedMapType));  }}