import 'package:google_maps_cluster_manager/google_maps_cluster_manager.dart';import 'package:google_maps_flutter/google_maps_flutter.dart';import '../../utils/cluster_utils.dart';class CustomMapMarker with ClusterItem {  final String? id;  final String? name;  final LatLng latLng;  final LatLng? myLocation;  final BitmapDescriptor? icon;  final StatusVals? status;  CustomMapMarker({    this.id,    this.name,    required this.latLng,    this.myLocation,    this.icon,    this.status,  });  @override  LatLng get location => latLng;  LatLng? get userLocation => myLocation;}