import 'package:google_maps_cluster_manager/google_maps_cluster_manager.dart';import 'package:google_maps_flutter/google_maps_flutter.dart';class CustomMapMarker with ClusterItem {  final String? id;  final String? name;  final LatLng latLng;  final BitmapDescriptor? icon;  CustomMapMarker({     this.id,     this.name,     required this.latLng,    this.icon,  });  @override  LatLng get location => latLng;}