import 'dart:async';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:google_maps_flutter/google_maps_flutter.dart';import 'package:samoilenko_maps_app/features/stations/bloc/stations_bloc/stations_cubit.dart';import 'package:samoilenko_maps_app/features/stations/bloc/stations_bloc/stations_cubit_state.dart';class MainMapWidget extends StatefulWidget {  const MainMapWidget({super.key});  // static const CameraPosition _kGooglePlex = CameraPosition(  //   target: LatLng(23.7985053, 90.3842538),  //   zoom: 14,  // );  @override  State<MainMapWidget> createState() => _MainMapWidgetState();}class _MainMapWidgetState extends State<MainMapWidget> {  final Completer<GoogleMapController> _controller = Completer<GoogleMapController>();  @override  void initState() {    super.initState();  }  @override  Widget build(BuildContext context) {    return BlocBuilder<StationsCubit, StationsCubitState>(      builder: (context, state) {        return state.when(          initial: () => const Center(            child: CircularProgressIndicator(),          ),          loading: () => const Center(            child: CircularProgressIndicator(),          ),          error: (String message) => Center(            child: Text(message),          ),          loaded: (stationsInfo) {            final items = stationsInfo;            Iterable _markers = Iterable.generate(items.length, (index) {              return Marker(                  markerId: MarkerId(items[index].stationId ?? '0'),                  position: LatLng(                    items[index].latitude ?? 0,                    items[index].longitude ?? 0,                  ),                  infoWindow: InfoWindow(title: 'title'));            });            CameraPosition _kGooglePlex = CameraPosition(              target: LatLng(items[0].latitude ?? 0, items[0].longitude ?? 0),              zoom: 10,            );            return GoogleMap(              initialCameraPosition: _kGooglePlex,              myLocationButtonEnabled: false,              markers: Set.from(_markers),              onMapCreated: (GoogleMapController controller) {                _controller.complete(controller);              },            );          },        );      },    );  }}