import 'package:extrawest_ui_kit/components/widgets/ew_text_field.dart';import 'package:flutter/material.dart';import '../../../common/router.dart';import '../../theme/theme_info.dart';class AppSearchBar extends StatefulWidget {  const AppSearchBar({    super.key,  });  @override  State<AppSearchBar> createState() => _AppSearchBarState();}class _AppSearchBarState extends State<AppSearchBar> {  final TextEditingController searchController = TextEditingController();  final focus = FocusNode();  @override  void initState() {    super.initState();    searchController.addListener(_navigateToSearchScreen);    focus.unfocus();  }  @override  void dispose() {    searchController.dispose();    focus.dispose();    super.dispose();  }  void _navigateToSearchScreen() {    goRouter.go(      '$homeRoute$stationSearchPageRoute',    );  }  @override  Widget build(BuildContext context) {    return Positioned(      top: 56,      child: Container(        height: 45,        ///full width minus padding        width: MediaQuery.of(context).size.width - 40,        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(10),          color: white,        ),        child: GestureDetector(          onTap: () {            // Prevent keyboard from showing on tap            FocusScope.of(context).unfocus();          },          child: EWTextField(            controller: searchController,            autoValidateMode: AutovalidateMode.disabled,            keyboardType: TextInputType.none,            prefixIcon: const Icon(Icons.search),            suffixIcon: const Text(''),          ),        ),        //todo: remove later        // InputTextWidget(        //   hintText: 'Type here',        //   focus: focus,        //   isReadonly: true,        //   onTapCallback: () {        //     GoRouter.of(context).go(        //       "/stations_search",        //       extra: {        //         'mapController': wid