import 'package:extrawest_ui_kit/components/widgets/ew_text_field.dart';import 'package:flutter/material.dart';import '../../theme/theme_info.dart';class AppSearchBar extends StatefulWidget {  const AppSearchBar({super.key});  @override  State<AppSearchBar> createState() => _AppSearchBarState();}class _AppSearchBarState extends State<AppSearchBar> {  final TextEditingController searchController = TextEditingController();  @override  void initState() {    super.initState();    searchController.addListener(() {});  }  @override  void dispose() {    super.dispose();  }  @override  Widget build(BuildContext context) {    return Positioned(      top: 56,      child: Container(        height: 45,        ///full width minus padding        width: MediaQuery.of(context).size.width - 40,        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(10),          color: white,        ),        child: EWTextField(          controller: searchController,          hintText: 'Type here',          prefixIcon: const Icon(Icons.search, color: greyIcon, size: 20),        ),      ),    );  }}