import 'package:flutter/material.dart';import '../../theme/theme_info.dart';class EwSearchBar extends StatefulWidget {  const EwSearchBar({super.key});  @override  State<EwSearchBar> createState() => _EwSearchBarState();}class _EwSearchBarState extends State<EwSearchBar> {  final TextEditingController searchController = TextEditingController();  @override  void initState() {    super.initState();    searchController.addListener(() {});  }  @override  void dispose() {    super.dispose();  }  @override  Widget build(BuildContext context) {    return Positioned(      top: 56,      child: Container(        ///full width minus padding        width: MediaQuery.of(context).size.width - 40,        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(10),          color: white,        ),        child: TextField(          controller: searchController,          decoration: const InputDecoration(            border: InputBorder.none,            hintText: 'Type here',            prefixIcon: Icon(Icons.search, color: greyIcon, size: 20),          ),        ),      ),    );  }}