import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:samoilenko_maps_app/features/wallet/widgets/wallet_presenter_widget.dart';import '../bloc/wallet_cubit.dart';class WalletScreen extends StatelessWidget {  const WalletScreen({super.key});  @override  Widget build(BuildContext context) {    return BlocProvider<WalletCubit>(      create: (_) => WalletCubit(        walletRepository: RepositoryProvider.of(context),      )..fetchWallet(),      child: const WalletPresenterWidget(),    );  }}